contracts/BlochPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./BlochToken.sol";
import "./USDQToken.sol";

contract BlochPool is Ownable {
    IERC20 public usdt;
    BlochToken public blochToken;
    USDQToken public usdqToken;

    uint256 public constant BLOCH_TOTAL_SUPPLY = 240_000_000 * 1e18; // Tổng cung 240 triệu BLOCH
    uint256 public soldBloch;
    uint256 public lockedUSDT;

    struct Phase {
        uint256 price; // Giá mỗi BLOCH trong giai đoạn này
        uint256 limit; // Số lượng BLOCH tối đa cho phép bán trong giai đoạn này
    }

    mapping(uint256 => Phase) public phases;
    uint256 public currentPhase;

    event BlochPurchased(address indexed buyer, uint256 amountBloch, uint256 amountUSDT, uint256 usdqMinted);

    constructor(IERC20 _usdt, BlochToken _blochToken, USDQToken _usdqToken) {
        usdt = _usdt;
        blochToken = _blochToken;
        usdqToken = _usdqToken;

        // Đặt thông tin cho các giai đoạn phát hành
        phases[1] = Phase(0.5 * 1e18, 60_000_000 * 1e18);   // 2025: Giá 0.5 USDT, giới hạn 60 triệu BLOCH
        phases[2] = Phase(1 * 1e18, 60_000_000 * 1e18);     // 2026: Giá 1 USDT, giới hạn 60 triệu BLOCH
        phases[3] = Phase(1.5 * 1e18, 60_000_000 * 1e18);   // 2027: Giá 1.5 USDT, giới hạn 60 triệu BLOCH
        phases[4] = Phase(2 * 1e18, 60_000_000 * 1e18);     // 2028: Giá 2 USDT, giới hạn 60 triệu BLOCH
    }

    function purchaseBloch(uint256 amountBloch) external {
        require(amountBloch > 0, "Amount must be greater than 0");
        Phase memory phase = phases[currentPhase];
        require(soldBloch + amountBloch <= phase.limit, "Purchase exceeds phase limit");

        uint256 amountUSDT = (amountBloch * phase.price) / 1e18;
        require(usdt.transferFrom(msg.sender, address(this), amountUSDT), "USDT transfer failed");

        blochToken.transfer(msg.sender, amountBloch);
        usdqToken.mint(msg.sender, amountUSDT);

        lockedUSDT += amountUSDT;
        soldBloch += amountBloch;

        emit BlochPurchased(msg.sender, amountBloch, amountUSDT, amountUSDT);
    }

    function setPhase(uint256 _phase) external onlyOwner {
        require(_phase >= 1 && _phase <= 4, "Invalid phase");
        currentPhase = _phase;
    }

    function withdrawLockedUSDT(address to) external onlyOwner {
        uint256 amount = lockedUSDT;
        lockedUSDT = 0;
        require(usdt.transfer(to, amount), "USDT transfer failed");
    }
}

contracts/BlochToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract BlochToken is ERC20, Ownable {
    constructor() ERC20("Bloch Token", "BLOCH") {
        _mint(msg.sender, 240_000_000 * (10 ** decimals())); // Tổng cung 240 triệu BLOCH
    }

    function burn(uint256 amount) external onlyOwner {
        _burn(msg.sender, amount);
    }
}

